@njit
def historic_average(X, h, future_xreg, residuals):
    y = X[:, 0] if X.ndim == 2 else X
    mean = np.repeat(y.mean(), h)
    fcst = {'mean': mean}
    if residuals:
        res = np.full(y.size, np.nan, y.dtype)
        res[1:] = y[1:] - y.cumsum()[:-1] / np.arange(1, y.size)
        fcst['residuals'] = res
    return fcst

